import {AllTests} from 'AllTests.js';

import {CorrectAnswersTests} from 'CorrectAnswersTests.js';

import {SOCIETYwords} from "society_words.js";
import {SPORTwords} from "sport_words.js";
import {ACCOMMODATIONwords} from "accommodation_words.js";
import {APPEARANCEwords} from "appearance_words.js";
import {PREPOSITIONSwords} from "prepositions_words.js";
import {NATUREwords} from "nature_words.js";
import {Jokes} from "jokes.js";


export function Global() {

	//initialisation -----------
	global.words_files = ["SOCIETY","SPORT","ACCOMMODATION", "APPEARANCE", "PREPOSITIONS", "NATURE"]; // words files
	global.words = []; // words array
	global.all_words = []; // array of all words
 	global.jokes_arr = []; // jokes array
	global.work_arr = []; // array of random numbers
	global.count_pick_button_show_word = 0;  //count of picking button_show_word
	global.count_pick_button_show_description = 0; //count of picking button_show_description
	global.current_number_word = 0;  //current number word
	global.current_number_word_group = 0; // current word group (SOCIETY)
	global.count_pick_button_show_answer = 0;
	global.nWords = 0; // quantity of words
  	global.nTests = 0; // quantity of tests
  	global.currentTest = 0; // current test
	global.countDownDate = new Date().getTime(); // start time for the chosing test
	global.countDownDate_qst = new Date().getTime(); // start time for the chosing question
  	global.count_time_question = [];  // all time for the current question
  	global.count_time_test = []; // all time for the current test
	global.current_question = 0; // current question  ( 0  -  first)
	global.X_setInterval = 0; // id of clearInterval
	
	global.idioms_files = ["JOB","HOT","MIX"]; // idioms files
  	
	/* initialize variables */
	global.count = 0; 
	global.clearTime = 0; 
	global.seconds = 0;
	global.minutes = 0;
	global.hours = 0; 
	global.clearState = 0; 
	global.secs = 0;
	global.mins = 0;
	global.gethours = 0; 


	// tests -----------------
	global.tests = AllTests(); // read all tests

	global.nTests = global.tests.length; // amount of all tetsts

	global.cAnswers = CorrectAnswersTests();  // read all correct answers

	global.count_questions_test = global.tests[global.currentTest].length; // amount of questions in current test
	
	global.selected_answer = -1;  // number of the selected answer in the current question

	global.count_correct_answer = [];  // amount of correct answers for earch question

	
	// words -----------------
	global.all_words[0] = SOCIETYwords();
	global.all_words[1] = SPORTwords();
	global.all_words[2] = ACCOMMODATIONwords();
	global.all_words[3] = APPEARANCEwords();
	global.all_words[4] = PREPOSITIONSwords();
	global.all_words[5] = NATUREwords();

	global.curent_number_word = 0;


	// LISTEN-READ --------------
	global.nAudio = 3;  // amount of all audio files


	// jokes -----------------
	global.jokes_arr = Jokes(); // read all jokes
	global.nJokes = global.jokes_arr.length; // amount of all jokes

	return global;
	
}
