import React from "react"

import {Global} from "Global.js";

let global = Global();

export class PlayCurrentAudio extends React.Component {
    constructor(props) {
        let  arr_state_audio = Array(global.nAudio);
        for(let i=0; i<global.nAudio; i++) {
        arr_state_audio[i] = true;
        }
        arr_state_audio[0] = false; 
        super(props);
        //this.handleChange = this.handleChange(this);//.bind(this);
        this.state = ({state_edit: false, 
                    state_test: arr_state_audio,  // arr_state_test[i] = false - i-question is active
                    })
    }// constructor

    readAudioText(){
        document.getElementById('read_cont').style.display = 'block';
        document.getElementById('button_read').style.display = 'none';
    }//---end of readAudioText

    playAudio(){
console.log('11111')        
        document.getElementById('play_cont').style.display = 'block';
        document.getElementById('button_play').style.display = 'none';
console.log('22222')            
    }//---end of playAudio



    render(){
    //console.log('render');    
        
        let ID_work;

        if(document.getElementById('tests_wrapper') !== null) document.getElementById('words_wrapper').style.display = 'none';
        if(document.getElementById('words_wrapper') !== null) document.getElementById('words_wrapper').style.display = 'none';
        if(document.getElementById('phrases_wrapper') !== null) document.getElementById('phrases_wrapper').style.display = 'none';
        if(document.getElementById('jokes_wrapper') !== null) document.getElementById('jokes_wrapper').style.display = 'none';
        document.getElementById('header_menu_tests').style.textDecoration = 'none';
        document.getElementById('header_menu_words').style.textDecoration = 'none';
        document.getElementById('header_menu_phrases').style.textDecoration = 'none';
        document.getElementById('header_menu_jokes').style.textDecoration = 'none';
    
        document.getElementById('listen_wrapper').style.display = 'block';
        document.getElementById('header_menu_listen').style.textDecoration = 'underline';
    
   
        let element = [];
    //console.log('this.state.state_edit=',this.state.state_edit);
    
        element[0] = (  <div id='button_play_cont'>
                            <div id='play_cont' display='none'>
                                <audio controls id='music'>  
                                    <source src='mj.mp3' type='audio/mpeg'></source> 
                                </audio>
                                <p className='common_text'>Click the buttons to play or pause the audio.</p>
                            </div>
                            <button id='button_play' key='0' onClick={this.playAudio}>Play</button> 
                        </div>
                     )
        element[1] = (<div id='button_read_cont'>
                        <div id='read_cont' display='none'></div>
                        <button id='button_read' key='1'  onClick={this.readAudioText}>Read</button>
                      </div>
        )

        return element;
    }//render

}//---end of ShowCurrentTest
