import React from "react"

export class Events extends React.Component {  
  state = {
    showDialog: true
  };
//export function Global() {

  handleDocumentClick = e => {
    // return element object or null
    const isClosest = e.target.closest(`[id=list_tests_wrapper]`).style.visibility = "visible";
//    document.getElementById('list_tests_wrapper').style.visibility = "visible";

    if (this.state.showDialog) {
      this.setState({ showDialog: false });
    }
  };

  componentDidMount() {
    document.addEventListener("mouseover", this.handleDocumentClick);
  }

  componentWillUnmount() {
    document.removeEventListener('mouseover', this.handleDocumentClick);
  }
  
  render() {
    return null;
  }

} //--- end of Events