import React from "react"

import ReactDOM from 'react-dom';

import {Global} from "Global.js";

import {ShowJokeTest} from "ShowJokeTest.js";

let global = Global();

// Title of Jokes Menu
//export function HeaderMenuJokes() {
export class HeaderMenuJokes extends React.Component {	

  changeVisibilityJokes(e) {
//    document.getElementById('list_jokes_wrapper').style.visibility = 'visible';
    if(document.getElementById('tests_wrapper') !== null) document.getElementById('tests_wrapper').style.display = 'none';
    if(document.getElementById('words_wrapper') !== null) document.getElementById('words_wrapper').style.display = 'none';
    if(document.getElementById('listen_wrapper') !== null) document.getElementById('listen_wrapper').style.display = 'none';
    if(document.getElementById('phrases_wrapper') !== null) document.getElementById('phrases_wrapper').style.display = 'none';

    document.getElementById('list_words_wrapper').style.visibility = 'hidden';
    document.getElementById('list_tests_wrapper').style.visibility = 'hidden';    
    document.getElementById('header_menu_tests').style.textDecoration = "none";
    document.getElementById('header_menu_words').style.textDecoration = "none";

    document.getElementById('jokes_wrapper').style.display = 'block';
  }

  showJokes() { 
//	console.log('1 global.current_number_word_group = ',global.current_number_word_group) ;	

    function ShowJokeWork() {
      if(document.getElementById('tests_wrapper') !== null) document.getElementById('tests_wrapper').style.display = 'none';
      if(document.getElementById('words_wrapper') !== null) document.getElementById('words_wrapper').style.display = 'none';
      if(document.getElementById('listen_wrapper') !== null) document.getElementById('listen_wrapper').style.display = 'none';
      if(document.getElementById('phrases_wrapper') !== null) document.getElementById('phrases_wrapper').style.display = 'none';
  
      document.getElementById('list_words_wrapper').style.visibility = 'hidden';
      document.getElementById('list_tests_wrapper').style.visibility = 'hidden';    
      document.getElementById('header_menu_tests').style.textDecoration = "none";
      document.getElementById('header_menu_words').style.textDecoration = "none";
  
      document.getElementById('jokes_wrapper').style.display = 'block';
      document.getElementById('jokes_wrapper').style.visibility = 'visible';

      return <ShowJokeTest />;

    }//---end of ShowWordWork

    ReactDOM.render(<ShowJokeWork />, document.getElementById('jokes_wrapper'));
     
  } //----end of  showJokes

  render() {
    return (<div id="header_menu_jokes" className="header_menu_item"  onClick={this.showJokes.bind(this)}>JOKES</div>) //onMouseEnter={this.changeVisibilityJokes}
  }

}//---end of HeaderMenuJokes()
