import React from "react"

export function HeaderMenuTests() {
//console.log('header_menu_tests')  

  return (<div id="header_menu_tests" className="header_menu_item" onMouseEnter={changeVisibilityTests}>Tests</div>)
  
  function changeVisibilityTests(e) {
    const dispatch = useDispatch();
    dispatch({type: 'SHOW_TESTS_MENU', action: 'visible'});

//    document.getElementById('list_words_wrapper').style.visibility = 'hidden';
//    document.getElementById('list_tests_wrapper').style.visibility = 'visible';
//    e.target.style.background = 'red';
  }
}

