export function HeaderMenuWords() {

  
let dispatch = useDispatch();
let visible_tests = useSelector(state => state.visible_tests);
let visible_words = useSelector(state => state.visible_words);
let visible_listen = useSelector(state => state.visible_listen);
let visible_phrases = useSelector(state => state.visible_phrases);
let visible_jokes = useSelector(state => state.visible_jokes);

let value_tests = useSelector(state => state.value_tests);
let value_words = useSelector(state => state.value_words);
let value_listen = useSelector(state => state.value_listen);
let value_phrases = useSelector(state => state.value_phrases);
let value_jokes = useSelector(state => state.value_jokes);

console.log('HeaderMenuWords: visible_tests=',visible_tests);  
console.log('HeaderMenuWords: visible_words=',visible_words);

function changeVisibilityWords(e) {
  console.log('changeVisibilityWords:',visible_words)
  document.getElementById('list_words_wrapper').style.visibility = visible_words;
  document.getElementById('list_tests_wrapper').style.visibility = visible_tests;
  document.getElementById('list_listen_wrapper').style.visibility = visible_listen;
  document.getElementById('list_phrases_wrapper').style.visibility = visible_phrases;
//    document.getElementById('list_jokes_wrapper').style.visibility = visible_jokes;

  document.getElementById('tests_wrapper').style.visibility = value_tests;
  document.getElementById('words_wrapper').style.visibility = value_words;
  document.getElementById('listen_wrapper').style.visibility = value_listen;
  document.getElementById('phrases_wrapper').style.visibility = value_phrases;
  document.getElementById('jokes_wrapper').style.visibility = value_jokes;

  return dispatch({type: 'SHOW_WORDS_MENU', action: 'visible'});
}//end of changeVisibilityWords --------

  return (<div id="header_menu_words" className="header_menu_item"  onMouseEnter={changeVisibilityWords}>WORDS</div>)

}//end of HeaderMenuWords -----
