import React from "react"

// Title of Words Menu
export function HeaderMenuWords() {

  return (<div id="header_menu_words" className="header_menu_item"  onMouseEnter={changeVisibilityWords}>WORDS</div>)

  function changeVisibilityWords(e) {
    document.getElementById('list_words_wrapper').style.visibility = 'visible';
    document.getElementById('header_menu_tests').style.textDecoration = "none";
    document.getElementById('list_tests_wrapper').style.visibility = 'hidden';
//    e.target.style.background = 'red';
  }

}