import React from "react"
import ReactDOM from 'react-dom';

import {Global} from "Global.js";

let global = Global();

// li_answer - analys of user choose among answers. 
//             li_number - choosing li-number in qst_number questions of number_test test
export function LiAnswer(props) {
console.log('props=',props);  
let arr = props.par;
    let li_number = arr[0];
    let qst_number = arr[1];    
    let number_test = arr[2];
console.log('li_number=',li_number, '  qst_number=', qst_number, '  number_test=',number_test);    
    let id_input = "id" + String(qst_number*10 + li_number);
    let number_answer = Number(global.tests[number_test-1][qst_number].C_A);
console.log('number_answer=',number_answer);    
    let id_correct =  "id" + String(qst_number*10 + (number_answer - 1));
console.log('id_input=',id_input,'  id_correct=',id_correct);
    if(id_input === id_correct){
      global.count_correct_answer = global.count_correct_answer + 1; 	
    }
console.log('global.count_correct_answer =',global.count_correct_answer)   
  
  } //---- function li_answer(li_number, qst_number, number_test) 
  