import React from "react"
import ReactDOM from 'react-dom';

import {Global} from "Global.js";

import {StartWatch4} from "StartWatch4.js";

import {QuestionTest} from "QuestionTest.js";

import {AnswerTest} from "AnswerTest.js";

let global = Global();

// ShowTest - Test with number nTest
export function ShowTest(numberTest) {

  global.curentTest = numberTest.i; // curent test
//console.log('global.curentTest=',global.curentTest);
  let questions_test = global.tests[global.curentTest-1];
//console.log('questions_test=',questions_test);  
  if(questions_test)  global.count_questions_test = questions_test.length
  else  global.count_questions_test = 0;
//console.log('global.count_questions_test=',global.count_questions_test);  

  let func = '';
  let j = 0;
  let si;
  let ID_time; // ID for time_test
  let ID_time_qst; // ID for time_tests_qst
  let ID_work;
  let style_div;

  // show  TESTS

  global.countDownDate = new Date().getTime(); // start time for the chosing test
  global.countDownDate_qst = new Date().getTime(); // start time for the chosing question
  global.count_correct_answer = 0;  // count of correct answers

  let element = [];
//console.log('global.count_questions_test=',global.count_questions_test);
  for(let ii=0; ii < global.count_questions_test; ii++) { // show all questions for the test

    // add <div class="question_cont">
    ID_work = 'ID'+ ii + global.curentTest*100 +  '_question_cont';   
    if (ii !== 0) {
      // hide all questions except 0
      element[ii] = 
      <div key={ID_work} id={ID_work} className="question_cont" style={{opacity: ".3"}}>
        <QuestionTest numberQuestion={ii} />
        <AnswerTest numberQuestion={ii} />
      </div>
    } else  element[ii] = 
    <div  key={ID_work} id={ID_work} className="question_cont">
      <QuestionTest numberQuestion={ii} />
      <AnswerTest numberQuestion={ii} />
    </div>;


  }  

//console.log('element=',element);
  ReactDOM.render(element, document.getElementById('tests_wrapper'));

} //---- function showTest(numberTest) {
 

