//Get position of the div: obj
// return: [top, left, bottom, right]

export function getPosDiv(obj){
    console.log('getPosDiv: obj=', obj);
    const objWidth = obj.width();
    const top = offsets.top;
    const objHeight = obj.height();
    const bottom = top - objHeight;
    const left = offsets.left;
    const right = left + objWidth;

    return [top, left, bottom, right];
}
